mapper.select:
select

from self_order

   order_state = #{orderState}
  and engineer_id = #{engineerId}

select

from self_order

   order_state = #{orderState}
  and customer_id = #{customerId}

select

from order_info

   update_time >= #{startDate}
   and update_time <= #{endDate}
  and engineer_id = #{engineerId}
  and customer_id = #{customerId}
   and order_state = 5
